// Зададим стартовую дату
var start = new Date(),
    prevDay,
    startHours = 9;

// // // 09:00
// start.setHours(9);
// start.setMinutes(0);

// $dp.datepicker().data('datepicker').update('minHours', startHours);

var $dp = $('.datepicker-here');

var dpOptns = {
  altField: $('.alt-datepicker'),
  altFieldDateFormat: 'dd.mm.yyyy',
  dateFormat: 'dd.mm.yyyy',
  // timepicker: true,
  // startDate: start,
  // minHours: startHours,
  // maxHours: 18,
  autoClose: true,
  // setDate: $(this).val(),
  // startDate: $(this).val(),
  // selectDate: $(this).el.value,
  onSelect: function(fd, d, picker) {
      // Ничего не делаем если выделение было снято
      if (!d) return;

      var day = d.getDay();

      // Обновляем состояние календаря только если была изменена дата
      if (prevDay != undefined && prevDay == day) return;
      prevDay = day;
  }
}

$dp.datepicker(dpOptns);


